package programmes;

public class MathematicalEquations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float addition;
		float multiplication;
		float division;
		float subtraction; 
		float x;
		float y;
		float z;
		x=35.0f;
		y=12.0f;
		z=8.0f;
		
		addition=x+y+z;
		multiplication=y*z;
		division=(x/z);
		subtraction=x-y;
		
	System.out.println("x+y+z="+addition+"\n");
	System.out.println("y x z ="+multiplication+"\n");	
	System.out.println("x/z="+division+"\n");
	System.out.println("x-y="+subtraction+"\n");
	
	
	}
}
